WITH dexs AS (SELECT t.evt_block_time AS block_time, t.to AS taker, '' AS maker, CASE WHEN CAST(amount0Out AS DOUBLE) = 0 THEN CAST(amount1Out AS DOUBLE) ELSE CAST(amount0Out AS DOUBLE) END AS token_bought_amount_raw, CASE WHEN CAST(amount0In AS DOUBLE) = 0 OR CAST(amount1Out AS DOUBLE) = 0 THEN CAST(amount1In AS DOUBLE) ELSE CAST(amount0In AS DOUBLE) END AS token_sold_amount_raw, NULL AS amount_usd, CASE WHEN CAST(amount0Out AS DOUBLE) = 0 THEN f.token1 ELSE f.token0 END AS token_bought_address, CASE WHEN CAST(amount0In AS DOUBLE) = 0 OR CAST(amount1Out AS DOUBLE) = 0 THEN f.token1 ELSE f.token0 END AS token_sold_address, t.contract_address AS project_contract_address, t.evt_tx_hash AS tx_hash, '' AS trace_address, t.evt_index FROM {{ source('uniswap_v2_ethereum','Pair_evt_Swap') }} AS t INNER JOIN {{ source('uniswap_v2_ethereum','Factory_evt_PairCreated') }} AS f ON f.pair = t.contract_address{% if is_incremental() %}  WHERE NOT t.contract_address IN ('{{weth_ubomb_wash_trading_pair}}', '{{weth_weth_wash_trading_pair}}', '{{feg_eth_wash_trading_pair}}') AND t.evt_block_time >= DATE_TRUNC('day', NOW() - INTERVAL '7' day /* week doesnt work in duneSQL */)) {% endif %} SELECT 'ethereum' AS blockchain, 'uniswap' AS project, '2' AS version, TRY_CAST(DATE_TRUNC('DAY', dexs.block_time) AS DATE) AS block_date, dexs.block_time, erc20a.symbol AS token_bought_symbol, erc20b.symbol AS token_sold_symbol, CASE WHEN LOWER(erc20a.symbol) > LOWER(erc20b.symbol) THEN CONCAT(erc20b.symbol, '-', erc20a.symbol) ELSE CONCAT(erc20a.symbol, '-', erc20b.symbol) END AS token_pair, CAST(token_bought_amount_raw AS DOUBLE) / POWER(10, erc20a.decimals) AS token_bought_amount, CAST(token_sold_amount_raw AS DOUBLE) / POWER(10, erc20b.decimals) AS token_sold_amount, TRY_CAST(CAST(token_bought_amount_raw AS DOUBLE) AS DECIMAL(38, 0)) AS token_bought_amount_raw, TRY_CAST(CAST(token_sold_amount_raw AS DOUBLE) AS DECIMAL(38, 0)) AS token_sold_amount_raw, COALESCE(CAST(amount_usd AS DOUBLE), (CAST(token_bought_amount_raw AS DOUBLE) / POWER(10, p_bought.decimals)) * p_bought.price, (CAST(token_sold_amount_raw AS DOUBLE) / POWER(10, p_sold.decimals)) * p_sold.price) AS amount_usd, dexs.token_bought_address, dexs.token_sold_address, COALESCE(dexs.taker, tx.from) AS taker /* subqueries rely on this COALESCE to avoid redundant joins with the transactions table */, dexs.maker, dexs.project_contract_address, dexs.tx_hash, tx.from AS tx_from, tx.to AS tx_to, dexs.trace_address, dexs.evt_index FROM dexs INNER JOIN {{ source('ethereum','transactions') }} AS tx ON tx.hash = dexs.tx_hash AND tx.block_time >= '{{project_start_date}}' AND tx.block_time >= DATE_TRUNC('day', NOW() - INTERVAL '7' day /* week doesnt work in duneSQL */) LEFT JOIN {{ ref('tokens_erc20') }} AS erc20a ON erc20a.contract_address = dexs.token_bought_address AND erc20a.blockchain = 'ethereum' LEFT JOIN {{ ref('tokens_erc20') }} AS erc20b ON erc20b.contract_address = dexs.token_sold_address AND erc20b.blockchain = 'ethereum' LEFT JOIN {{ source('prices','usd') }} AS p_bought ON p_bought.minute = DATE_TRUNC('minute', dexs.block_time) AND p_bought.contract_address = dexs.token_bought_address AND p_bought.blockchain = 'ethereum' AND p_bought.minute >= '{{project_start_date}}' AND p_bought.minute >= DATE_TRUNC('day', NOW() - INTERVAL '7' day /* week doesnt work in duneSQL */) LEFT JOIN {{ source('prices','usd') }} AS p_sold ON p_sold.minute = DATE_TRUNC('minute', dexs.block_time) AND p_sold.contract_address = dexs.token_sold_address AND p_sold.blockchain = 'ethereum' AND p_sold.minute >= '{{project_start_date}}' AND p_sold.minute >= DATE_TRUNC('day', NOW() - INTERVAL '7' day /* week doesnt work in duneSQL */)